mapper.select:
select 

from es_goods
where goods_id = #{goodsId,jdbcType=INTEGER}

SELECT m.user_name, g. NAME gname, c.content, m.head_portrait_url, m.nickname, m.id AS userid, c.grade, g.goods_id FROM es_member_comment c LEFT JOIN es_goods g ON c.goods_id = g.goods_id LEFT JOIN u_user m ON c.member_id = m.id WHERE c.type = 1 AND c.status = 1
and g.goods_id = #{goods_id,jdbcType=INTEGER} ORDER BY c.comment_id DESC
LIMIT #{pageNo},#{pageSize}

SELECT u.goods_id,o.price,u.mktprice,o.product_id,u.name,o.name AS pname,o.specs,o.store,u.thumbnail,
       o.enable_store,u.reduction , u.credit from es_goods u LEFT JOIN es_product o ON u.goods_id = o.goods_id
       where u.goods_id=#{goods_id,jdbcType=INTEGER}

select DISTINCT name,cat_id  from es_goods_cat  where cat_id in(
select DISTINCT substring(cat_path, 3, 1) from es_goods_cat where cat_id in (

select distinct cat_id from es_goods where brand_id=#{brand_id,jdbcType=INTEGER}
  and market_enable=1
)
)
